import React, { Component } from 'react';
import axios from "axios";
import ContentList from "../component/contentList/ContentList.js";

class Search extends Component{
    constructor(props){
        super(props);
        this.state = {
            contents: [],
            keyword: ""
        };
    }

    handleInputChange = e => {
        this.setState({keyword: e.target.value});
    };

    //유투브 
    fetchSearch = keyword => {
        const maxResult = 30; //몇개의 데이터를 받을꺼냐?        
        const token = "AIzaSyC-v1sIG2Wn3YnoD_7_bBS4zPDceDLKmLY"; //
        
        axios.get("https://www.googleapis.com/youtube/v3/search?q=" 
        + keyword 
        + "&part=snippet&key=" 
        + token 
        + "&maxResults=" 
        + maxResult
    ).then( ({data}) => { //원래는 data인데 역직렬화 하려고  {(data)} 한다
        console.log(data);
        const list = this.setContents(data);
        this.setState({ contents: list });
     });
    };

    handleSubmit = e =>{
        this.fetchSearch(this.state.keyword);
        e.preventDefault();
    }

    setContents = (data) => { 
        let list = []
         data.items.forEach((item, index) => {
             list.push({ id:item.id.videoId,name:item.snippet.title })
        })
        return list
    }

    render(){
        //this.fetchSearch("영화");
        return(
            <>
                <div className="Search">
                    <form onSubmit={this.handleSubmit}>
                        <div className="form-group row align-items-center justify-content-center">
                            <div className="col-md-3">
                                <label>검색</label>
                                <input
                                    type="text"
                                    className="form-control keyword"
                                    placeholder="Search.."
                                    onChange={this.handleInputChange}
                                    />
                            </div>
                        </div>
                    </form>
                    <ContentList contents={this.state.contents} />
                </div>
            </>
        )
    }
}

export default Search;
